import React from "react";
import { Slide } from "react-slideshow-image";
import "./Slider.css";

const slideImages = [
  "https://images-eu.ssl-images-amazon.com/images/G/31/img17/AmazonDevices/2019/Herotator/GW-Dec/FTV_PC-GW-hero_1500x600_1x._CB446558100_.jpg",
  "https://images-eu.ssl-images-amazon.com/images/G/31/img17/AmazonDevices/2019/Herotator/GW-Dec/FTV_PC-GW-hero_1500x600_1x._CB446558100_.jpg",
];

const properties = {
  duration: 5000,
  transitionDuration: 500,
  infinite: true,
  indicators: true,
  arrows: true,
};

function Slider() {
  return (
    <Slide {...properties}>
      <div className="home_image">
        <img src={slideImages[0]}  alt=""/>
      </div>
      <div className="home_image">
      <img src={slideImages[1]}  alt=""/>
      </div>
    </Slide>
  );
}

export default Slider;
