import React from "react";
import "./Home.css";
import Product from "./Product";

function Home() {
  return (
    <div className="home">
      <div className="home_container">
        <div class="slideshow-container">
          <div class="slides fade">
            <img
              className="home_image"
              src="https://images-eu.ssl-images-amazon.com/images/G/31/img17/AmazonDevices/2019/Herotator/GW-Dec/FTV_PC-GW-hero_1500x600_1x._CB446558100_.jpg"
              alt=""
            />
          </div>

          <div class="slides fade">
            <img
              className="home_image"
              src="https://images-eu.ssl-images-amazon.com/images/G/31/img20/Wireless/SamsungM/M51/8thSept_GW/P38983965_IN_WLME_SamsungGalaxy_M51_New_Launch_DesktopTallHero_2_1500x600._CB405103024_.jpg"
              alt=""
            />
          </div>

          <a class="prev" onclick="plusSlides(-1)">
            &#10094;
          </a>
          <a class="next" onclick="plusSlides(1)">
            &#10095;
          </a>
        </div>

        <div className="home_row">
          {/* {Product} */}
          <Product
            id="001"
            title="HP Pavilion Gaming DK0268TX 15.6-inch Laptop,Shadow
          Black"
            price={67490}
            image="https://images-na.ssl-images-amazon.com/images/I/81pezrPSgOL._SL1500_.jpg"
            rating={5}
          />
          <Product
            id="002"
            title="Samsung Galaxy S10 Plus (White, 8GB RAM, 128GB Storage)"
            price={52999}
            image="https://images-na.ssl-images-amazon.com/images/I/61dvlh%2BCsqL._SL1500_.jpg"
            rating={4}
          />

          <Product
            id="002"
            title="Samsung Galaxy S10 Plus (White, 8GB RAM, 128GB Storage)"
            price={52999}
            image="https://images-na.ssl-images-amazon.com/images/I/61dvlh%2BCsqL._SL1500_.jpg"
            rating={4}
          />
          {/* {Product} */}
        </div>

        <div className="home_row">
          {/* {Product} */}
          <Product
            title="HP Pavilion Gaming DK0268TX 15.6-inch Laptop (Core i5-9300H/8GB/512GB
            SSD/Windows 10 Home/4GB NVIDIA GeForce GTX 1650 Graphics), Shadow
            Black"
            price={67490}
            image="https://images-na.ssl-images-amazon.com/images/I/81pezrPSgOL._SL1500_.jpg"
            rating={5}
          />
          {/* {Product} */}
          <Product
            title="HP Pavilion Gaming DK0268TX 15.6-inch Laptop (Core i5-9300H/8GB/512GB
            SSD/Windows 10 Home/4GB NVIDIA GeForce GTX 1650 Graphics), Shadow
            Black"
            price={67490}
            image="https://images-na.ssl-images-amazon.com/images/I/81pezrPSgOL._SL1500_.jpg"
            rating={5}
          />
        </div>

        <div className="home_row">
          <Product
            title="HP Pavilion Gaming DK0268TX 15.6-inch Laptop (Core i5-9300H/8GB/512GB
          SSD/Windows 10 Home/4GB NVIDIA GeForce GTX 1650 Graphics), Shadow
          Black"
            price={67490}
            image="https://images-na.ssl-images-amazon.com/images/I/81pezrPSgOL._SL1500_.jpg"
            rating={5}
          />
        </div>
      </div>
    </div>
  );

  var slideIndex = 1;
  showSlides(slideIndex);

  // Next/previous controls
  function plusSlides(n) {
    showSlides((slideIndex += n));
  }

  // Thumbnail image controls
  function currentSlide(n) {
    showSlides((slideIndex = n));
  }

  function showSlides(n) {
    var i;
    var slides = document.getElementsByClassName("slides");
    if (n > slides.length) {
      slideIndex = 1;
    }
    if (n < 1) {
      slideIndex = slides.length;
    }
    for (i = 0; i < slides.length; i++) {
      slides[i].style.display = "none";
    }

    slides[slideIndex - 1].style.display = "block";
    
  }
}

export default Home;
